
public class DivisibleNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int x =49;
        if (x%5 ==0) {
    	System.out.println(x+ "is divisible by 5");
    
    }
        else if(x%5 !=0) {
    	System.out.println(x+ "is not divisible by 5");
    }
	}

}
